import { HR, IconsDiv, Input, NewPostIcon, New_Post, New_Posts_Div } from "@/app/styles/Feed/Feed"
import { Formik, useFormik } from "formik";
import * as Yup from "yup";


const addPost = () => {

  const formik = useFormik({
    initialValues: {
      firstName: '',
      search: "",
      
     
    },
    validationSchema: Yup.object().shape({
      firstName:  Yup.string().required("search  cannot be empty") ,
      search: Yup.string().min(7, 'min 7 characters required'),
     
    }),
    onSubmit: values => {
      console.log(values);
    }
  });

  const { handleSubmit, handleChange, values, errors , touched} = formik;
  return (
    <form onSubmit={handleSubmit}>
     <New_Posts_Div>
          <New_Post>NEW POST</New_Post>
          <HR></HR>
          <div>
            <IconsDiv>
              <NewPostIcon
                src="https://cdn-icons-png.flaticon.com/128/659/659774.png"
                alt=""
              />
              <NewPostIcon
                src="data:image/png;base64,"
                alt=""
              />
              <NewPostIcon
                src="https://d1nhio0ox7pgb.cloudfront.net/_img/o_collection_png/green_dark_grey/512x512/plain/film.png"
                alt=""
              />
              <NewPostIcon
              onClick={() => console.log("დაეკლიკა!")}
              style={{cursor: "pointer"}}
                src="https://cdn-icons-png.flaticon.com/512/3024/3024593.png"
                alt=""
              />
            </IconsDiv>
          </div>
          <input
        type="text"
        name="search"
        placeholder="Whats on"
        value={values.search}
        onChange={handleChange}
        style={{
          border: touched.search && errors.search ? '1px solid red' : '4px solid #ccc',
         
        }}
       
      />
      {errors.search && touched.search ? (
        <div style={{ color: 'red' }}>{errors.search}</div>
      ) : null}
      <button type="submit">Submit</button>
        </New_Posts_Div> 
        </form>
  )
}

export default addPost;